mapper.select:
select bno, title, userid, regdate, views from board 

	 where title like #{fval}


	 where userid like #{fval}


	 where contents like #{fval}

 order by bno desc limit #{snum}, 25

select ceil(count(bno)/25) cnt from board

	 where title like #{fval}


	 where userid like #{fval}


	 where contents like #{fval}
